import 'package:flutter/material.dart';
class CardExample extends StatelessWidget{
  const CardExample({Key? key}) :super(key: key);

  @override
  Widget build(BuildContext context){
    return Card(
      clipBehavior: Clip.antiAlias,
      elevation: 10,
      shadowColor: Colors.blue,
      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(20)),
      child: Column(
        children: [
          imageStack(),
          Padding(
            padding: const EdgeInsets.all(12.0),
            child: Text('The easiest way to introduce yourself on GitHub is by creating a README in a repository about you! You can start here:',
            style: TextStyle(fontSize: 16),
            textAlign: TextAlign.justify,
            ),
          ),
          ButtonBar(
            alignment: MainAxisAlignment.start,
            children: [
              FlatButton(onPressed: (){}, child: Text('Read')),
              FlatButton(onPressed: (){}, child: Text('View')),
            ],
          )
        ],
      ),
    );
  }
}

Widget imageStack(){
  String url = 'data:image/jpeg;base64,';

  return Stack(
    children:[
      Ink.image(image: NetworkImage(url),
      height: 200,
      fit: BoxFit.cover,
      ),
      Positioned(
        bottom: 16,
        left: 16,
        child: Text(
          'Cute dog', 
      style: TextStyle(
        fontSize: 24, fontWeight: FontWeight.bold, color: Colors.white
      ),
      ),
     ),
    ],
  );
}